mapper.delete:
delete from common_dict
where dict_type = #{dictType,jdbcType=CHAR}
and dict_value = #{dictValue,jdbcType=CHAR}

mapper.update:
update common_dict
set dict_label = #{dictLabel,jdbcType=VARCHAR},
value_length = #{valueLength,jdbcType=INTEGER},
remark = #{remark,jdbcType=VARCHAR}
where dict_type = #{dictType,jdbcType=CHAR}
and dict_value = #{dictValue,jdbcType=CHAR}

mapper.select:
select dict_type, dict_value, dict_label, value_length, remark
from common_dict
where dict_type = #{dictType,jdbcType=CHAR}
and dict_value = #{dictValue,jdbcType=CHAR}
